INSERT INTO `ls_article` VALUES ('393', '项目销售经理', '计算机学院', '<p>				</p><h4 style=\";margin-bottom:0;background:white\"><span style=\"font-size: 19px;font-family: 微软雅黑, sans-serif;color: red;font-weight: normal\">项目销售经理：</span></h4><p><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">职位月薪：面议</span></p><p><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">工作地点：广州</span></p><p><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">发布日期：2015-12-08</span></p><p><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">招聘人数：1人</span></p><h4 style=\";margin-bottom:0;background:white\"><span style=\"font-size: 19px;font-family: 微软雅黑, sans-serif;color: red;font-weight: normal\">职位描述：</span></h4><p><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">1</span><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">、根据公司销售目标完成销售任务；</span></p><p><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">2</span><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">、分析市场，开拓市场；</span></p><p style=\";margin-bottom:0;line-height:22px;vertical-align:baseline\"><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">3</span><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">、做好销售服务工作。</span></p><p><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">4</span><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">、负责销售部门的日常运营工作</span></p><p><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">5</span><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#333333\">、负责销售部管理（年度销售计划、目标的制定、任务分解）</span></p><h4 style=\";margin-bottom:0;background:white\"><span style=\"font-size: 19px;font-family: 微软雅黑, sans